List<Disease> doencas = new List<Disease>();
  
setProperty(){
  caxumba.name="Caxumba";
  caxumba.description = "\n\n Caxumba é uma infecção viral que afeta uma dos três pares de glândulas que produzem saliva, estão situadas entre suas orelhas e à frente delas. Contudo, a caxumba também pode afetar as glândulas próximas dos ouvidos. A caxumba é muito mais comum em crianças e pode afetar uma das glândulas ou todas elas. As complicações da caxumba são raras, e geralmente acontecem quando a pessoa contrai a doença na vida adulta. Não há tratamento específico para caxumba, por isso é importante prevenir seu aparecimento com a vacina tetra ou tríplice viral.";
  sarampo.name="Sarampo";
  sarampo.description = "\n\n A transmissão ocorre diretamente, de pessoa a pessoa, geralmente por tosse, espirros, fala ou respiração, por isso a facilidade de contágio da doença. Além de secreções respiratórias ou da boca, também é possível se contaminar através da dispersão de gotículas com partículas virais no ar, que podem perdurar por tempo relativamente longo no ambiente, especialmente em locais fechados como escolas e clínicas. Os sintomas iniciais apresentados pelo doente são: febre acompanhada de tosse persistente, irritação ocular, coriza e congestão nasal e mal estar intenso. Após estes sintomas, há o aparecimento de manchas avermelhadas no rosto, que progridem em direção aos pés, com duração mínima de três dias. São comuns lesões muito dolorosas na boca. A doença pode ser grave, com acometimento do sistema nervoso central e pode complicar com infecções secundárias como pneumonia, podendo levar à morte. As complicações atingem mais gravemente os desnutridos, os recém-nascidos, as gestantes e as pessoas portadoras de imunodeficiências. Não há um remédio específico para a doença – mais um motivo para não deixar de se vacinar. O paciente deve se manter sempre hidratado e bem alimentado, enquanto os médicos lidam com as consequências do quadro, como diarreia. A suscetibilidade ao vírus do sarampo é geral e a única forma de prevenção é a vacinação. Apenas os lactentes cujas mães já tiveram sarampo ou foram vacinadas possuem, temporariamente, anticorpos transmitidos pela placenta, que conferem imunidade geralmente ao longo do primeiro ano de vida (o que pode interferir na resposta à vacinação).";
  rubeola.name="Rubéola";
  rubeola.description = "\n\n A rubéola é uma doença aguda, de alta contagiosidade, que é transmitida por um vírus m. A doença  também é conhecida como Sarampo Alemão. Sintomas: febre baixa, surgimento de gânglios linfáticos e de manchas rosadas, que se espalham primeiro pelo rosto e depois pelo resto do corpo. A prevenção da rubéola é feita por meio das vacinas tetra e tríplice viral. As vacinas estão disponíveis nos postos de saúde para crianças a partir de 12 meses de idade.";
  catapora.name="Catapora";
  catapora.description="\n\n A catapora é uma infecção causada por um vírus e marcada por bolhinhas vermelhas que coçam e se espalham pelo corpo todo. Os sintomas, no entanto, aparecem entre o décimo e o 21º dia da infecção, que demora ainda mais 20 dias para ir embora de vez. Altamente transmissível, o vírus se espalha pelo ar e também pelo contato com as lesões. Por isso é comum mais de um integrante da família ficar com catapora. A catapora é uma das doenças que fazem parte do calendário nacional de vacinação infantil. A vacina contra ela, tetra viral, ainda protege contra rubéola, caxumba e sarampo. Mas ainda existem surtos do problema pelo país, especialmente durante a primavera. Na maioria das vezes, a catapora não é considerada uma enfermidade grave.";
  varicela.name = "Varicela";
  varicela.description = "\n\n A varicela, uma infecção muito frequente na infância, é causada por um vírus chamado varicela-zoster. A infecção é normalmente ligeira em crianças, embora os bebês recém-nascidos e os adultos possam ficar muito doentes se apanharem a varicela. Os primeiros sintomas são febre entre 37,5° e 39,5° C, mal-estar, inapetência, dor de cabeça, cansaço. Entre 24 e 48 horas mais tarde, surgem lesões de pele caracterizadas por manchas avermelhadas, que dão lugar a pequenas bolhas ou vesículas cheias de líquido, sobre as quais, posteriormente, se formarão crostas que provocam muita coceira.  A vacina contra varicela é recomendada para crianças a partir de um ano, a adolescentes e adultos com baixa imunidade ou que passarão por tratamentos de quimioterapia e radioterapia.";
  tuberculose.name="Tuberculose";
  tuberculose.description="\n\n Doença contagiosa que afeta principalmente os pulmões mas pode ser encontrada em outros órgãos. Alguns sintomas comuns são, tosse seca e muito suor durante a noite. Pode ser prevenida através da vacina BCG que deve ser dada às crianças ao nascer, ou no máximo, até os 5 anos completos.";
  hepatitea.name="Hepatite A";
  hepatitea.description="\n\n Inflamação do fígado causada por um vírus. Alguns sintomas são fadiga, xixi escura, enjoos, vômitos e amarelamento da pele e olhos. Melhor jeito de previnir essa doença  é a através da vacina da hepatite A que é dividida em duas fases: a inicial e o reforço após seis meses da primeira dose.";
  hepatiteb.name="Hepatite B";
  hepatiteb.description="\n\n Doença infecciosa, presente no sangue, no leite materno e no esperma, considerada uma doença sexualmente transmissível. Pode ser prevenida através das três doses da vacina da hepatite B, a utilização da camisa em todas as relações sexuais e o não compartilhamento de objetos de uso pessoal. ";
  rotavirus.name="Rota Virus";
  rotavirus.description="\n\n É uma das mais importantes causas da diarreia grave em crianças menores de cinco anos no mundo. Sintomas: diarréia com aspecto aguado, gorduroso e explosivo, febre alta, entre outros. Prevenido pela vacina para rotavírus humano G1P1[8] em crianças menores de seis meses, feita por via oral, sendo a  primeira dose aos dois meses e a segunda aos quatro meses de idade com intervalo mínimo de 30 dias entre as doses.";
  hpv.name="HPV";
  hpv.description="\n\n Vírus que infecta pele ou mucosas (peledo nariz, mamilos, boca e genitais), provocando verrugas anogenitais( região genital e ânus) e câncer, dependendo do tipo do vírus, sendo sexualmente transmissível. A medida eficaz para prevenção é a vacina contra o HPV, indicado para meninas de 9 a 14 anos e meninos de 11 a 14 anos, pessoas com HIV e pessoas transplantadas de 9 a 26 anos.";
  gripe.name="Gripe";
  gripe.description="\n\n É causada por o vírus da Influenza, que causa febre alta, seguida de dor muscular, dor de garganta, dor de cabeça, ranho e tosse.  Alguns casos apresentam complicações graves, como pneumonia e precisam de internação hospitalar. Vacinas: Existem as vacinas trivalente e a quadrivalente.  Nos convênios existe a quadrivalente, que é contra os dois subtipos A (H1N1 e H3N2) e os dois subtipos B (que dependem do vírus circulante no ano anterior). Na rede pública é oferecida e trivalente, que consiste nos dois tipos da Influenza A (H1N1 e o H3N2) e um vírus da Influenza B.";
  pneumonia.name="Pneumonia";
  pneumonia.description="\n\n Há dois tipos de vacinas disponíveis contra o pneumococo: a vacina de polissacarídeo 23 valente e a vacina conjugada heptavalente. As duas oferecem proteção contra alguns sorotipos causadores de infecção por esta bactéria.";
  difteria.name="Difteria";
  difteria.description="\n\n Várias bactérias invadem o corpo e se alojam principalmente na garganta, nariz e na pele, elas causam uma dor de garganta leve, mal-estar, febre não muito alta, dificuldade para respirar, gânglios inchados (localizados no pescoço). A Difteria é transmitida por tosse, espirro ou machucados na pele. É uma doença muito grave que leva muitas vezes a morte. Pode ser evitada pela vacinação. Vacina pentavalente/DTP ou dTpa. Utiliza-se a mesma vacina para Difteria, Tétano e Coqueluche.";
  tetano.name="Tetano";
  tetano.description="\n\n Ocorre em quando bactérias entram no corpo através de ferimentos, não é uma doença contagiosa. Essa doença leva ao endurecimento dos músculos e contrações musculares muito doloridas, especialmente na mandíbula e no pescoço, pode causar febre, pressão alta, cólicas, torcicolo, coração acelerado, dificuldade em respirar, entre outros. Pode levar a morte. Essa doença é prevenida com a vacinação. Utiliza-se a mesma vacina para Difteria, Tétano e Coqueluche. ";
  coqueluche.name="Coqueluche";
  coqueluche.description="\n\n Especialmente perigosa para bebês, é uma doença respiratória que causa mal estar, muito ranho, tosse seca e febre baixa, com o tempo a tosse passa a ser descontrolada, o que dificulta a respiração. Adultos e adolescentes apresentam sintomas mais leves do que crianças pequenas. Essa doença pode levar a morte e é prevenida com a vacinação. Utiliza-se a mesma vacina para Difteria, Tétano e Coqueluche.";
  tetanoneonatal.name="Tetano Neonatal";
  tetanoneonatal.description="\n\n É uma doença infecciosa grave que ataca crianças nos seus primeiros 28 dias de vida. É transmitido durante a manejo do cordão umbilical ou do maneja inadequado do coto umbilical.";
  hepatitesvirais.name = "Hepatites Virais";
  hepatitesvirais.description="\n\n Existem 3 tipos de hepatite. A transmissão da hepatite A pode ocorrer pelo contato fecal-oral por meio do consumo de água e alimentos contaminados por fezes; condições precárias de saneamento básico, falta de higiene pessoal e por meio do sexo oral-anal.\n\nRelações sexuais sem preservativo, compartilhar seringas, agulhas, lâminas de barbear, alicates de unha e outros objetos que furam ou cortam, fazer tatuagem e/ou colocar piercing em estabelecimentos que não utilizam material descartável e/ou esterilizado podem levar a contaminação do vírus B, C e D, transmitidos pelo sangue. Esses vírus também podem passar da mãe para o filho durante a gravidez, o parto e a amamentação.\n\nA hepatite é uma inflamação do fígado que pode ser causada por vírus ou pelo uso de alguns remédios, álcool e outras drogas, assim como por doenças autoimunes, metabólicas e genéticas. São doenças silenciosas que nem sempre apresentam sintomas, mas, quando estes aparecem, podem ser cansaço, febre, mal-estar, tontura, enjoo, vômitos, dor abdominal, pele e olhos amarelados, urina escura e fezes claras.\n\nA adoção de medidas preventivas é a melhor forma de evitar a contaminação pelos vírus da hepatite. No caso das hepatites B e C, recomenda-se evitar contato com sangue infectado como não compartilhando objetos cortantes ou perfurantes, nem instrumentos para preparação de drogas injetáveis. Outra recomendação importante é sempre usar preservativo nas relações sexuais, além de realizar procedimentos de tatuagem e colocação de piercing somente em estabelecimentos que utilizam material descartável e/ou esterilizado.\n\nAlém disso, manter a higiene pessoal, com particular ênfase na lavagem frequente das mãos e da região genital e perianal, antes e após a relação sexual, lavar bem os alimentos crus e consumir apenas água potável são medidas de prevenção da contaminação por hepatite A.\n\nExistem vacinas específicas para a hepatite A e hepatite B disponíveis no sistema público de saúde. A vacina contra a hepatite A é fornecida para crianças entre 15 meses e menores de 5 anos e a vacina contra a hepatite B para todas as faixas etária.";
  sifilis.name = "Sífilis";
  sifilis.description = "\n\n Sífilis é uma doença sexualmente transmissível,  é contagiosa nos estágios iniciais, raramente a sífilis pode ser transmitida pelo beijo. \nO primeiro estágio da sífilis é o que apresenta feridas indolores (cancros) e rasas no local da infecção, normalmente na região genital. Não existe mais nenhum sintoma e a ferida desaparece sozinha. Não é possível observar mais sintomas. A bactéria “dorme” (torna-se inativa) no organismo. \nO segundo estágio da sífilis acontece cerca de duas a oito semanas após as primeiras feridas se formarem. Nessa fase a pessoa contaminada pode apresentar vermelhidão pelo corpo, coceira, aparecimento de íngua (gânglios inchados) localizados nas axilas e pescoço. \nA terceira fase da sífilis é a  mais difícil de ser detectada, pois têm sintomas em grandes vasos (como a aorta), cérebro, olhos, coração, juntas e até mesmo dentro do sistema nervoso. Ai ela pode causar dor de cabeça, epilepsia, e é um diagnóstico um pouco mais complicado. \nSífilis latente Esse é o período correspondente ao estágio inativo da sífilis, em que não há sintomas. Esse estágio pode perdurar por muito tempo sem que a pessoa sinta nada. A doença pode nunca mais se manifestar no organismo, mas pode ser que ela se desenvolva para o próximo estágio, o terciário – e mais grave de todos. \nSífilis congênita \nO tratamento para sífilis mais indicado pelos médicos é feito à base de penicilina um antibiótico. Uma única injeção de penicilina já é o bastante para impedir a progressão da sífilis, principalmente se ela for aplicada no primeiro ano após a infecção. Se não, o paciente poderá precisar de mais de uma injeção. A penicilina, aliás, é o único tratamento recomendado por especialistas para mulheres grávidas diagnosticadas com sífilis. Mesmo que o tratamento nesses casos seja bem-sucedido, o bebê também deverá ser tratado com antibióticos depois de nascer.";
  poliomielite.name="Poliomielite";
  poliomielite.description="Conhecida também por pólio ou por paralisia infantil, é uma doença muito contagiosa que atinge adultos e crianças, por meio do contato com fezes e excreções saídas da boca de pessoas contaminadas. Os sintomas da pólio são febre, mal-estar, dor na garganta, cabeça e corpo, vômitos, diarreia, prisão de ventre, nuca rígida e espasmos. Essa doença pode ser evitada através da vacinação injetável (3 doses) e pela vacinação oral (2 doses, gotinha). É uma doença irreversível, portanto é muito importante a vacinação de crianças pequenas antes do início dos sintomas. Pode levar a morte.";
  febreamarela.name="Febre Amarela";
  febreamarela.description="A febre amarela é uma doença infecciosa grave, causada por vírus e transmitida por mosquitos. Geralmente, quem contrai este vírus não chega a apresentar sintomas ou os mesmos são muito fracos. As primeiras manifestações da doença são repentinas: febre alta, calafrios, cansaço, dor de cabeça, dor muscular, náuseas e vômitos por cerca de três dias. A forma mais grave da doença é rara e costuma aparecer após um breve período de bem-estar (até dois dias), quando podem ocorrer manifestações hemorrágicas, cansaço intenso, olhos e pele amarelados. A medida eficaz contra a febre amarela é a vacina.";

  doencas.add(catapora);
  doencas.add(caxumba);
  doencas.add(coqueluche);
  doencas.add(difteria);
  doencas.add(febreamarela);
  doencas.add(gripe);
  doencas.add(hepatitea);
  doencas.add(hepatiteb);
  doencas.add(hepatitesvirais);
  doencas.add(hpv);
  doencas.add(pneumonia);
  doencas.add(poliomielite);
  doencas.add(rotavirus);
  doencas.add(rubeola);
  doencas.add(sarampo);
  doencas.add(sifilis);
  doencas.add(tetano);
  doencas.add(tetanoneonatal);
  doencas.add(tuberculose);
  doencas.add(varicela);

}

